for each in `gh project item-list --limit 800 --format json --owner fleetdm 67 |  jq -r '["number", "assignee", "sprint", "estimate", "labels", "status"], (.items[] | [.content.number, (.assignees[0] // "unassigned"), .sprint.title, (.estimate // "none"), (.status // "none"), (.labels | join(", "))]) | @tsv' | column -t -s $'\t' | grep " Estimated" | grep "#g-mdm" | grep -v ":release" | awk '{print $1}'`; do echo $each; gh issue edit $each --add-label ":release"; gh issue edit $each --remove-label ":product"; done
